                    return { dir: 'up', text: m === 'temp' ? '🌡️ อุณหภูมิสูงผิดปกติ' : '💧 ความชื้นสูงผิดปกติ', score: 1 };
                } else if (rg.min != null && alert.trigger_value < rg.min) {
                    return { dir: 'down', text: m === 'temp' ? '🌡️ อุณหภูมิต่ำผิดปกติ' : '💧 ความชื้นต่ำผิดปกติ', score: 1 };
