function dewPointC(temp, rh) {
  if (typeof temp !== 'number' || typeof rh !== 'number' || !isFinite(temp) || !isFinite(rh) || rh <= 0) return null;
  const RH = Math.min(100, Math.max(0, rh));
  const a = 17.62, b = 243.12;
  const gamma = (a * temp) / (b + temp) + Math.log(RH / 100.0);
  return (b * gamma) / (a - gamma);
}
